import React from "react";
import GraphComponent from "./GraphComponent";
import defaultImage from "../Assets/testImage.jpg";
import TopBar from "./TopBar";
import SpotifyWebApi from "spotify-web-api-js";

const spotifyAPI = new SpotifyWebApi();

// props:
// signout: function to remove spotify auth token cookie
// token: spotify token
export default class HomePage extends React.Component {
  constructor(props) {
    super(props);

    if (this.props.token) {
      spotifyAPI.setAccessToken(this.props.token);
    }

    this.state = {
      currentSong: "",
      albumCover: defaultImage,
      interval: null,
    }
  }

  componentDidMount = () => {
    this.state.interval = setInterval(this.getNowPlaying, 1000);
  }

  componentWillUnmount = () => {
    clearInterval(this.state.interval);
  }

  getNowPlaying = () => {
    spotifyAPI.getMyCurrentPlaybackState().then((data) => {
      this.setState({
          currentSong: data.item.name,
          albumCover: data.item.album.images[0].url,
        });
    });
  }

  render() {
    return (
      <div className="mainContent">
        <TopBar logout={this.props.signout} />
        <div className="albumContainer">
          <img
            className="albumCover"
            src={this.state.albumCover}
            alt={defaultImage}
          ></img>
        </div>
        <GraphComponent />
      </div>
    );
  }
}
