import React from "react";
import {Line} from "react-chartjs-2";

export default class GraphComponent extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      hrReadings: [], // List containing the heart rate readings to display in the graph (y axis)
      xAxis: [],
      maxReadings: 10, // Maximum # of readings stored in the hrReadings (simulate motion of the graph)
      options: {
        scales: {
          yAxes: [{
            gridLines: {
              drawBorder: false,
            },
          }],
          xAxes: [{
            gridLines: {
              display: false,
            },
          }],
        },
      },
    };
  }

  addItem = () => {

  };

  render() {
    const data = {
        labels: this.state.xAxis,
        datasets: [
          {
            data: this.state.hrReadings,
            fill: false,
            backgroundColor: 'rgb(255, 99, 132)',
            borderColor: 'rgba(255, 99, 132, 0.2)',
          },
        ],
      };

    return (
      <div>
        <Line options={this.state.options} data={data}/>
        <button onClick={this.addItem}>Add Point</button>
      </div>
    );
  }
}
