import React from "react";
import { Line } from "react-chartjs-2";

export default class GraphComponent extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      hrReadings: [90,90,90,90,130,130], // List containing the heart rate readings to display in the graph (y axis)
      xAxis: [0,0.5,1,1.5,2,2.5],
      maxReadings: 10, // Maximum # of readings stored in the hrReadings (simulate motion of the graph)
      options: {
        elements: {
            point: {
                radius:0,
            }
        },
        plugins: {
            legend: {
                display:false
            }
        },
        scales: {
          x: {
              display:false,
          },
          y: {
              display: false,
              suggestedMin:40,
              suggestedMax: 200,
          }
        },
      },
    };
  }

  addItem = () => {
      if (this.state.hrReadings.length >= this.state.maxReadings) {
        var lastVal = this.state.hrReadings[9];
        var lastX = this.state.xAxis[9];
        var newY = this.state.hrReadings.slice(1).push(lastVal + 1);
        var newX = this.state.xAxis.slice(1).push(lastX + 0.5);
        this.setState({xAxis:newX, hrReadings: newY});

      }
  };

  render() {
    const data = {
      labels: this.state.xAxis,
      datasets: [
        {
          data: this.state.hrReadings,
          fill: false,
          backgroundColor: "rgb(255, 99, 132)",
          borderColor: "rgb(255, 99, 132)",
          borderWidth: 5,
          tension: .3,
        },
      ],
    };

    return (
      <div>
        <Line className="lineGraph" options={this.state.options} data={data} />
        <button onClick={this.addItem}>Add Point</button>
      </div>
    );
  }
}
