import React from "react";
import CanvasJSReact from "../assets/canvasjs.react";

var CanvasJS = CanvasJSReact.CanvasJS;
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

export default class GraphComponent extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      hrReadings: [], // List containing the heart rate readings to display in the graph
      maxReadings: 10, // Maximum # of readings stored in the hrReadings (simulate motion of the graph)
    };
  }

  addItem = () => {
    var newList = this.state.hrReadings.concat({x:1, y:1});
    this.setState({hrReadings: newList})
  }

  render() {
    const options = {
      title: {
        text: "Basic Column Chart in React",
      },
      data: [
        {
          type: "line",
          dataPoints: this.state.hrReadings,
        },
      ],
    };

    return (
      <div>
        <CanvasJSChart
          options={options}
          /* onRef = {ref => this.chart = ref} */
        />
        <button onClick={this.addItem}>Add Point</button>
      </div>
    );
  }
}
