import React from "react";
import { Line } from "react-chartjs-2";

export default class GraphComponent extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      hrReadings: [1,2,3,4], // List containing the heart rate readings to display in the graph (y axis)
      xAxis: [1,2,3,4],
      maxReadings: 10, // Maximum # of readings stored in the hrReadings (simulate motion of the graph)
      options: {
        scales: {
          x: {
              display:false,
          },
          y: {
              display: false,
          }
        },
        tooltips: {
            callbacks: {
               label: function(tooltipItem) {
                      return tooltipItem.yLabel;
               }
            }
        }
      },
    };
  }

  addItem = () => {};

  render() {
    const data = {
      labels: this.state.xAxis,
      datasets: [
        {
          label: "",
          data: this.state.hrReadings,
          fill: false,
          backgroundColor: "rgb(255, 99, 132)",
          borderColor: "rgb(255, 99, 132)",
        },
      ],
    };

    return (
      <div>
        <Line options={this.state.options} data={data} />
        <button onClick={this.addItem}>Add Point</button>
      </div>
    );
  }
}
