import React from "react";
import GraphComponent from "./GraphComponent";
import defaultImage from "../Assets/testImage.jpg";
import TopBar from "./TopBar";
import SpotifyPlayer from "react-spotify-web-playback";
// import SpotifyWebApi from "spotify-web-api-js";

// const spotifyAPI = new SpotifyWebApi();

// props:
// signout: function to remove spotify auth token cookie
// token: spotify token
export default class HomePage extends React.Component {
  constructor(props) {
    super(props);

    // if (this.props.token) {
    //   spotifyAPI.setAccessToken(this.props.token);
    // }

    this.state = {
      currentSong: "",
      albumCover: defaultImage,
    };
  }

  componentDidMount = () => {

  }

  displayAlbum = (state) => {
    console.log(state.track.name);
  }

  render() {
    return (
      <div className="mainContent">
        <TopBar logout={this.props.signout} />
        <SpotifyPlayer
          token={this.props.token}
          callback={this.displayAlbum}
          uris={['spotify:artist:6HQYnRM4OzToCYPpVBInuU']}
          styles={{
            bgColor:'#000',
            sliderColor:'#ff6384',
            trackArtistColor: "#fff",
            trackNameColor: '#fff',
            color: '#fff',
          }}
        />
      </div>
    );
  }
}

